import argparsefrom helper import *import osimport shutilparser = argparse.ArgumentParser(description="Creating text file in data directory")parser.add_argument('directory', help='Name of directory inside data/')directory = parser.parse_args().directorylines_per_piece = 100output12_dir = 'exp/chain_cleaned_1d/tdnn1d_sp/output12_' + directorysplit_scp_dir = output12_dir + '/split_scp'scps = [file for file in list_non_hidden(output12_dir) if file.endswith('.scp')]try:    scps.remove('output.scp')except ValueError:    passfor file in scps:    shutil.copyfile(output12_dir + '/' + file, split_scp_dir + '/' + file)    for scp in scps:    f = open(split_scp_dir + '/' + scp, 'r')    lines = f.read().split('\n')    f.close()    try:        while True:            lines.remove('')    except ValueError:        pass            pieces = len(lines) // lines_per_piece + 1        for i in range(pieces):        start = i * lines_per_piece        end = start + lines_per_piece                new_filename = scp.replace('.scp', '_' + str(i) + '.scp')        f = open(split_scp_dir + '/' + new_filename, 'w')        for j in range(start, end):            try:                f.write(lines[j] + '\n')            except IndexError:                assert i == pieces - 1                break                f.close()        os.remove(split_scp_dir + '/' + scp)